mapper.select:
SELECT b1.pharmacy_item_id pharmacyItemId, b1.pharmacy_item_name pharmacyItemName, b1.pharmacy_item_type pharmacyItemType, b1.specifications specifications, b1.manufacturer manufacturer, b1.stock_unit stockUnit, a1.stock_detail_id stockDetailId, a1.expire_date expireDate, a1.stock_count stockCount, a1.selling_price sellingPrice FROM ( SELECT b.pharmacy_item_id, b.pharmacy_item_name, b.pharmacy_item_type, b.specifications, b.manufacturer, b.stock_unit FROM pharmacy_item b WHERE 1 = 1
AND ( b.pharmacy_item_name LIKE CONCAT('%', #{keywords}, '%') OR LOWER(b.full_pinyin) LIKE CONCAT('%', LOWER(#{keywords}), '%') OR UPPER(b.abbreviation) LIKE CONCAT('%', UPPER(#{keywords}), '%') OR b.barcode = #{keywords} )
AND b.pharmacy_item_type = #{pharmacyItemType}
) b1, ( SELECT a.stock_detail_id, a.pharmacy_item_id, a.expire_date, a.stock_count, a.selling_price FROM stock_detail a WHERE a.stock_state = 1 ) a1 WHERE b1.pharmacy_item_id = a1.pharmacy_item_id
