mapper.select:
SELECT a.pur_order_id purOrderId, a.pur_order_code purOrderCode, a.pur_order_date purOrderDate, a.supplier_id supplierId, b.supplier_name supplierName, d.item_name itemName, a.is_entry entry, a.total_price totalPrice FROM pur_order a, pur_supplier b, ( SELECT b1.pur_order_id, GROUP_CONCAT(b1.item_name SEPARATOR '，') item_name FROM pur_order_detail b1 GROUP BY b1.pur_order_id ) d WHERE a.supplier_id = b.supplier_id AND a.pur_order_id = d.pur_order_id
AND date_format(a.create_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND d.item_name LIKE concat('%', #{itemName}, '%')
AND a.supplier_id = #{supplierId}
ORDER BY a.is_entry ASC, a.create_time DESC LIMIT ${offSet},${pageSize}

SELECT count(1) FROM pur_order a, ( SELECT a1.pur_order_id, GROUP_CONCAT(b1.item_name SEPARATOR '，') item_name FROM pur_order a1, pur_order_detail b1 WHERE a1.pur_order_id = b1.pur_order_id GROUP BY a1.pur_order_id ) d WHERE a.pur_order_id = d.pur_order_id
AND date_format(a.create_time,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND d.item_name LIKE concat('%', #{itemName}, '%')
AND a.supplier_id = #{supplierId}
