mapper.select:
SELECT a.pur_stock_id purStockId, a.item_id itemId, a.item_name itemName, c.dict_name itemType, CONCAT(0 + CAST(b.stock_warn AS CHAR),'（',a.stock_unit_name,'）') stockWarn, CONCAT(0 + CAST(a.stock_count AS CHAR),'（',a.stock_unit_name,'）') stockCount FROM pur_stock a, item_purchase b, sys_dictionary c WHERE a.item_id = b.item_id AND a.stock_count <= b.stock_warn AND a.stock_state = 1 AND c.dict_key = '${@com.littledoctor.clinicassistant.common.constant.DictionaryKey@ITEM_PMFL}' AND a.item_type = c.dict_value AND c.is_use = 1
AND ( b.item_name LIKE CONCAT('%', #{keywords}, '%') OR LOWER(b.full_pinyin) LIKE CONCAT('%', LOWER(#{keywords}), '%') OR UPPER(b.abbr_pinyin) LIKE CONCAT('%', UPPER(#{keywords}), '%') -- OR b.barcode = #{keywords} )
LIMIT ${offset},${pageSize}

SELECT b1.item_id itemId, b1.item_name itemName, b1.poisonous poisonous, b1.usage_and_dosage usageAndDosage, b1.taboo taboo, b1.stock_unit stockUnit, b1.specifications specifications, b1.manufacturer manufacturer, b1.item_type itemType, a1.selling_price sellingPrice, a1.stock_detail_id stockDetailId, a1.stock_count stockCount FROM ( SELECT b.item_id, b.item_name, b.poisonous, b.usage_and_dosage, b.taboo, b.stock_unit, b.specifications, b.manufacturer, b.item_type FROM item_purchase b WHERE ( b.item_name LIKE CONCAT('%', #{keywords}, '%') OR LOWER(b.full_pinyin) LIKE CONCAT('%', LOWER(#{keywords}), '%') OR UPPER(b.abbr_pinyin) LIKE CONCAT('%', UPPER(#{keywords}), '%') )
AND b.item_type = #{itemType}
) b1, ( SELECT a.item_id, a.selling_price, GROUP_CONCAT(a.stock_detail_id ORDER BY a.stock_detail_id) stock_detail_id, SUM(a.stock_count) stock_count FROM stock_detail a WHERE a.stock_count > 0 AND a.stock_state = 1 GROUP BY a.item_id, a.selling_price ) a1 WHERE b1.item_id = a1.item_id
