mapper.select:
SELECT a.pur_stock_id purStockId, i.pur_item_name purItemName, d.dict_name dictName, a.batch_number batchNumber, a.expire_date expireDate, CONCAT(0 + CAST(a.stock_count AS CHAR),'（',d1.dict_name,'）') stockCount, REPLACE (FORMAT(a.selling_price, 4),',','') sellingPrice FROM pur_stock a, pur_item i, sys_dictionary d, sys_dictionary d1 WHERE a.pur_item_id = i.pur_item_id AND i.pur_item_type = d.dict_value AND i.stock_unit = d1.dict_value AND d.dict_key = '${@com.littledoctor.clinicassistant.common.constant.DictionaryKey@PUR_ITEM_CGPMFL}' AND d1.dict_key = '${@com.littledoctor.clinicassistant.common.constant.DictionaryKey@PUR_ITEM_LSDW}' AND a.stock_state = 1 AND d.is_use = 1 AND d1.is_use = 1
AND ( i.pur_item_name LIKE CONCAT('%', #{keywords}, '%') OR LOWER(i.full_pinyin) LIKE CONCAT('%', LOWER(#{keywords}), '%') OR UPPER(i.abbr_pinyin) LIKE CONCAT('%', UPPER(#{keywords}), '%') OR i.barcode = #{keywords} )
LIMIT ${offset},${pageSize}

SELECT b1.pur_item_id purItemId, b1.pur_item_name purItemName, b1.poisonous poisonous, b1.usage_and_dosage usageAndDosage, b1.taboo taboo, b1.stock_unit stockUnit, b1.specifications specifications, b1.manufacturer manufacturer, b1.pur_item_type purItemType, a1.selling_price sellingPrice, a1.stock_detail_id stockDetailId, a1.stock_count stockCount FROM ( SELECT b.pur_item_id, b.pur_item_name, b.poisonous, b.usage_and_dosage, b.taboo, b.stock_unit, b.specifications, b.manufacturer, b.pur_item_type FROM pur_item b WHERE ( b.pur_item_name LIKE CONCAT('%', #{keywords}, '%') OR LOWER(b.full_pinyin) LIKE CONCAT('%', LOWER(#{keywords}), '%') OR UPPER(b.abbr_pinyin) LIKE CONCAT('%', UPPER(#{keywords}), '%') )
AND b.pur_item_type = #{purItemType}
) b1, ( SELECT a.pur_item_id, a.selling_price, GROUP_CONCAT(a.stock_detail_id ORDER BY a.stock_detail_id) stock_detail_id, SUM(a.stock_count) stock_count FROM stock_detail a WHERE a.stock_count > 0 AND a.stock_state = 1 GROUP BY a.pur_item_id, a.selling_price ) a1 WHERE b1.pur_item_id = a1.pur_item_id
